project/contracts/ConfigCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title ConfigCore
 * @notice Immutable configuration core - stores critical system addresses
 * @dev Cannot be changed after deployment, ensuring system core architecture stability
 * @dev All core addresses are set once via constructor and permanently fixed
 */
contract ConfigCore {
    // ==================== Core Token Addresses (immutable) ====================

    /// @notice WBTC token address - used for collateral to mint BTD
    /// @dev Cannot be changed after deployment
    address public immutable WBTC;

    /// @notice BTD stablecoin address - primary stablecoin
    /// @dev Cannot be changed after deployment
    address public immutable BTD;

    /// @notice BTB bond token address - redemption bonds
    /// @dev Cannot be changed after deployment
    address public immutable BTB;

    /// @notice BRS governance token address - governance token
    /// @dev Cannot be changed after deployment
    address public immutable BRS;

    /// @notice WETH token address - Wrapped ETH
    /// @dev Cannot be changed after deployment
    address public immutable WETH;

    /// @notice USDC token address - stablecoin reserve
    /// @dev Cannot be changed after deployment
    address public immutable USDC;

    /// @notice USDT token address - stablecoin reserve
    /// @dev Cannot be changed after deployment
    address public immutable USDT;

    // ==================== Core Contract Addresses (storage - deferred binding) ====================
    // These 5 contracts have circular dependencies with ConfigCore, so they use storage and are set via setCoreContracts()

    /// @notice Treasury contract address - manages system assets
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public TREASURY;

    /// @notice Minter contract address - handles BTD minting and redemption
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public MINTER;

    /// @notice Price oracle address - provides WBTC price data
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public PRICE_ORACLE;

    /// @notice IUSD manager address - manages Ideal USD inflation adjustments
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public IDEAL_USD_MANAGER;

    /// @notice Interest pool address - manages BTD and BTB interest distribution
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public INTEREST_POOL;

    /// @notice Flag indicating whether core contracts have been set
    /// @dev Ensures setCoreContracts() can only be called once
    bool public coreContractsSet;

    /// @notice Flag indicating whether peripheral contracts have been set
    /// @dev Ensures setPeripheralContracts() can only be called once
    bool public peripheralContractsSet;

    // ==================== Price Oracle Data Source Addresses (immutable) ====================

    /// @notice Chainlink BTC/USD price feed address
    /// @dev Cannot be changed after deployment
    address public immutable CHAINLINK_BTC_USD;

    /// @notice Chainlink WBTC/BTC price feed address
    /// @dev Cannot be changed after deployment
    address public immutable CHAINLINK_WBTC_BTC;

    /// @notice Pyth WBTC price feed address
    /// @dev Cannot be changed after deployment
    address public immutable PYTH_WBTC;

    /// @notice Redstone WBTC price feed address
    /// @dev Cannot be changed after deployment
    address public immutable REDSTONE_WBTC;

    // ==================== Core Pool Addresses (deferred binding) ====================

    /// @notice Staking router address - handles staking operation routing
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public STAKING_ROUTER;

    /// @notice Farming pool address - BRS liquidity mining
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public FARMING_POOL;

    /// @notice stBTD token address - BTD staking receipt
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public ST_BTD;

    /// @notice stBTB token address - BTB staking receipt
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public ST_BTB;

    /// @notice Governor contract address - DAO governance
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public GOVERNOR;

    /// @notice TWAP oracle address - time-weighted average price oracle
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public TWAP_ORACLE;

    // ==================== Uniswap V2 Pool Addresses (deferred binding) ====================

    /// @notice WBTC-USDC Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_WBTC_USDC;

    /// @notice BTD-USDC Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_BTD_USDC;

    /// @notice BTB-BTD Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_BTB_BTD;

    /// @notice BRS-BTD Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_BRS_BTD;

    /**
     * @notice Constructor - sets all non-circular-dependency addresses
     * @dev The 5 core contracts with circular dependencies are set separately via setCoreContracts()
     */
    constructor(
        address _wbtc,                 // WBTC token address
        address _btd,                  // BTD stablecoin address
        address _btb,                  // BTB bond token address
        address _brs,                  // BRS governance token address
        address _weth,                 // WETH token address
        address _usdc,                 // USDC token address
        address _usdt,                 // USDT token address
        address _chainlinkBtcUsd,      // Chainlink BTC/USD price feed address
        address _chainlinkWbtcBtc,     // Chainlink WBTC/BTC price feed address
        address _pythWbtc,             // Pyth WBTC price feed address
        address _redstoneWbtc          // Redstone WBTC price feed address
    ) {
        require(_wbtc != address(0), "Invalid WBTC");
        require(_btd != address(0), "Invalid BTD");
        require(_btb != address(0), "Invalid BTB");
        require(_brs != address(0), "Invalid BRS");
        require(_weth != address(0), "Invalid WETH");
        require(_usdc != address(0), "Invalid USDC");
        require(_usdt != address(0), "Invalid USDT");
        require(_chainlinkBtcUsd != address(0), "Invalid Chainlink BTC/USD");
        require(_chainlinkWbtcBtc != address(0), "Invalid Chainlink WBTC/BTC");
        require(_pythWbtc != address(0), "Invalid Pyth WBTC");
        require(_redstoneWbtc != address(0), "Invalid Redstone WBTC");

        WBTC = _wbtc;
        BTD = _btd;
        BTB = _btb;
        BRS = _brs;
        WETH = _weth;
        USDC = _usdc;
        USDT = _usdt;
        CHAINLINK_BTC_USD = _chainlinkBtcUsd;
        CHAINLINK_WBTC_BTC = _chainlinkWbtcBtc;
        PYTH_WBTC = _pythWbtc;
        REDSTONE_WBTC = _redstoneWbtc;
    }

    /**
     * @notice Sets the 5 core contract addresses with circular dependencies
     * @dev Can only be called once, permanently locked after deployment
     * @param _treasury Treasury contract address
     * @param _minter Minter contract address
     * @param _priceOracle Price oracle address
     * @param _idealUSDManager IUSD manager address
     * @param _interestPool Interest pool address
     */
    function setCoreContracts(
        address _treasury,
        address _minter,
        address _priceOracle,
        address _idealUSDManager,
        address _interestPool
    ) external {
        require(!coreContractsSet, "Core contracts already set");
        require(_treasury != address(0), "Invalid Treasury");
        require(_minter != address(0), "Invalid Minter");
        require(_priceOracle != address(0), "Invalid PriceOracle");
        require(_idealUSDManager != address(0), "Invalid IdealUSDManager");
        require(_interestPool != address(0), "Invalid InterestPool");

        TREASURY = _treasury;
        MINTER = _minter;
        PRICE_ORACLE = _priceOracle;
        IDEAL_USD_MANAGER = _idealUSDManager;
        INTEREST_POOL = _interestPool;
        coreContractsSet = true;
    }

    /**
     * @notice Sets peripheral contracts and pools with circular dependencies
     * @dev Can only be called once, permanently locked after deployment
     */
    function setPeripheralContracts(
        address _stakingRouter,
        address _farmingPool,
        address _stBTD,
        address _stBTB,
        address _governor,
        address _twapOracle,
        address _poolWbtcUsdc,
        address _poolBtdUsdc,
        address _poolBtbBtd,
        address _poolBrsBtd
    ) external {
        require(!peripheralContractsSet, "Peripheral contracts already set");
        require(_stakingRouter != address(0), "Invalid StakingRouter");
        require(_farmingPool != address(0), "Invalid FarmingPool");
        require(_stBTD != address(0), "Invalid stBTD");
        require(_stBTB != address(0), "Invalid stBTB");
        require(_governor != address(0), "Invalid Governor");
        require(_twapOracle != address(0), "Invalid TWAPOracle");
        require(_poolWbtcUsdc != address(0), "Invalid Pool WBTC/USDC");
        require(_poolBtdUsdc != address(0), "Invalid Pool BTD/USDC");
        require(_poolBtbBtd != address(0), "Invalid Pool BTB/BTD");
        require(_poolBrsBtd != address(0), "Invalid Pool BRS/BTD");

        STAKING_ROUTER = _stakingRouter;
        FARMING_POOL = _farmingPool;
        ST_BTD = _stBTD;
        ST_BTB = _stBTB;
        GOVERNOR = _governor;
        TWAP_ORACLE = _twapOracle;
        POOL_WBTC_USDC = _poolWbtcUsdc;
        POOL_BTD_USDC = _poolBtdUsdc;
        POOL_BTB_BTD = _poolBtbBtd;
        POOL_BRS_BTD = _poolBrsBtd;
        peripheralContractsSet = true;
    }
}

project/contracts/ConfigGov.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/Constants.sol";

/**
 * @title ConfigGov - Governable Parameter Configuration Contract
 * @notice Manages runtime-adjustable system parameters (fees, limits, oracle addresses, etc.)
 * @dev Separated from ConfigCore: Core manages immutable addresses, Gov manages mutable parameters
 *      Governance logic can be upgraded without affecting core architecture
 */
contract ConfigGov is Ownable {

    /**
     * @notice Parameter type enum (uint256 type)
     * @dev Used for unified management of all system parameters, supports unlimited extension
     */
    enum ParamType {
        MINT_FEE_BP,        // 0 - Minting fee rate (basis points)
        INTEREST_FEE_BP,    // 1 - Interest fee rate (basis points)
        MIN_BTB_PRICE,      // 2 - BTB minimum price (18 decimals)
        MAX_BTB_RATE,       // 3 - BTB maximum interest rate (18 decimals)
        PCE_MAX_DEVIATION,  // 4 - PCE maximum deviation rate (18 decimals, e.g., 2e16 = 2%)
        REDEEM_FEE_BP       // 5 - Redemption fee rate (basis points)
        // Future extensions: LIQUIDATION_FEE, STABILITY_FEE, etc.
    }

    /**
     * @notice Address parameter type enum
     * @dev Used for managing governable oracle addresses, external contract addresses, etc.
     */
    enum AddressParamType {
        PCE_FEED          // 0 - Chainlink PCE oracle address
        // Future extensions: BACKUP_PCE_FEED, CPI_FEED, etc.
    }

    // ============ Storage ============

    /// @notice Unified parameter registry (governable fees, limits, and other parameters)
    mapping(ParamType => uint256) private _params;

    /// @notice Address parameter registry (governable oracle addresses, etc.)
    mapping(AddressParamType => address) private _addressParams;

    // ============ Events ============

    /// @notice Generic parameter update event (uint256 type)
    event ParamUpdated(ParamType indexed paramType, uint256 newValue);

    /// @notice Address parameter update event
    event AddressParamUpdated(AddressParamType indexed paramType, address newValue);

    // ============ Initialization ============

    /**
     * @notice Constructor - initializes governance parameters
     * @param initialOwner Contract owner address
     */
    constructor(
        address initialOwner
    ) Ownable(initialOwner) {
        require(initialOwner != address(0), "ConfigGov: zero owner");
    }

    // ============ Parameter Management ============

    /**
     * @notice Sets a single system parameter
     * @dev Only owner can call, used for updating fees, price limits, and other parameters
     * @param paramType Parameter type (from ParamType enum)
     * @param value New parameter value
     */
    function setParam(ParamType paramType, uint256 value) external onlyOwner {
        // Parameter range validation
        _validateParam(paramType, value);

        _params[paramType] = value;
        emit ParamUpdated(paramType, value);
    }

    /**
     * @notice Validates parameter range
     * @dev Internal function to ensure parameters are within reasonable bounds
     * @param paramType Parameter type
     * @param value Parameter value
     */
    function _validateParam(ParamType paramType, uint256 value) private pure {
        if (paramType == ParamType.MINT_FEE_BP) {
            require(value >= 1, "ConfigGov: mint fee too low");  // Minimum 0.01% (1 basis point)
            require(value <= 1000, "ConfigGov: mint fee too high"); // Maximum 10%
        } else if (paramType == ParamType.INTEREST_FEE_BP) {
            require(value >= 1, "ConfigGov: interest fee too low");
            require(value <= 1000, "ConfigGov: interest fee too high");
        } else if (paramType == ParamType.REDEEM_FEE_BP) {
            require(value >= 1, "ConfigGov: redeem fee too low");
            require(value <= 1000, "ConfigGov: redeem fee too high");
        } else if (paramType == ParamType.MIN_BTB_PRICE) {
            require(value >= 1e17, "ConfigGov: min BTB price too low"); // Minimum 0.1 BTD
            require(value <= 2e18, "ConfigGov: min BTB price too high"); // Maximum 2 BTD
        } else if (paramType == ParamType.MAX_BTB_RATE) {
            require(value >= 1e16, "ConfigGov: max BTB rate too low"); // Minimum 1% APR
            require(value <= 2e17, "ConfigGov: max BTB rate too high"); // Maximum 20% APR
        } else if (paramType == ParamType.PCE_MAX_DEVIATION) {
            require(value >= 1e15, "ConfigGov: PCE deviation too low"); // Minimum 0.1%
            require(value <= 1e17, "ConfigGov: PCE deviation too high"); // Maximum 10%
        }
    }

    /**
     * @notice Batch sets multiple system parameters
     * @dev Only owner can call, array lengths must be equal
     * @param paramTypes Parameter type array
     * @param values Corresponding new parameter value array
     */
    function setParamsBatch(
        ParamType[] calldata paramTypes,
        uint256[] calldata values
    ) external onlyOwner {
        require(paramTypes.length == values.length, "ConfigGov: length mismatch");
        for (uint i = 0; i < paramTypes.length; i++) {
            // Parameter range validation
            _validateParam(paramTypes[i], values[i]);

            _params[paramTypes[i]] = values[i];
            emit ParamUpdated(paramTypes[i], values[i]);
        }
    }

    /**
     * @notice Gets the value of a specified system parameter type
     * @dev Returns 0 if the parameter is not set
     * @param paramType Parameter type
     * @return Parameter value
     */
    function getParam(ParamType paramType) external view returns (uint256) {
        return _params[paramType];
    }

    // ============ Convenience Access Functions ============

    /**
     * @notice Gets the minting fee rate
     * @return Minting fee rate (basis points, e.g., 50 = 0.5%)
     */
    function mintFeeBP() external view returns (uint256) {
        return _params[ParamType.MINT_FEE_BP];
    }

    function interestFeeBP() external view returns (uint256) {
        return _params[ParamType.INTEREST_FEE_BP];
    }

    function minBTBPrice() external view returns (uint256) {
        return _params[ParamType.MIN_BTB_PRICE];
    }

    function maxBTBRate() external view returns (uint256) {
        return _params[ParamType.MAX_BTB_RATE];
    }

    /**
     * @notice Gets PCE maximum deviation rate
     * @dev Used to prevent abnormal PCE data fluctuations
     * @return PCE maximum deviation rate (18 decimals, e.g., 2e16 = 2%)
     */
    function pceMaxDeviation() external view returns (uint256) {
        return _params[ParamType.PCE_MAX_DEVIATION];
    }

    /**
     * @notice Gets the redemption fee rate
     * @dev Fee deducted from user when redeeming BTD
     * @return Redemption fee rate (basis points, e.g., 50 = 0.5%)
     */
    function redeemFeeBP() external view returns (uint256) {
        return _params[ParamType.REDEEM_FEE_BP];
    }

    // ============ Address Parameter Management ============

    /**
     * @notice Sets a single address parameter
     * @dev Only owner can call, used for updating oracle addresses, etc.
     * @param paramType Address parameter type (from AddressParamType enum)
     * @param value New address value
     */
    function setAddressParam(AddressParamType paramType, address value) external onlyOwner {
        require(value != address(0), "ConfigGov: zero address");
        _addressParams[paramType] = value;
        emit AddressParamUpdated(paramType, value);
    }

    /**
     * @notice Batch sets multiple address parameters
     * @dev Only owner can call, array lengths must be equal
     * @param paramTypes Address parameter type array
     * @param values Corresponding new address value array
     */
    function setAddressParamsBatch(
        AddressParamType[] calldata paramTypes,
        address[] calldata values
    ) external onlyOwner {
        require(paramTypes.length == values.length, "ConfigGov: length mismatch");
        for (uint i = 0; i < paramTypes.length; i++) {
            require(values[i] != address(0), "ConfigGov: zero address");
            _addressParams[paramTypes[i]] = values[i];
            emit AddressParamUpdated(paramTypes[i], values[i]);
        }
    }

    /**
     * @notice Gets the value of a specified address parameter type
     * @dev Returns address(0) if the parameter is not set
     * @param paramType Address parameter type
     * @return Address value
     */
    function getAddressParam(AddressParamType paramType) external view returns (address) {
        return _addressParams[paramType];
    }

    /**
     * @notice Gets PCE Feed oracle address
     * @return PCE Feed address
     */
    function pceFeed() external view returns (address) {
        return _addressParams[AddressParamType.PCE_FEED];
    }
}

