// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title ITreasury - Standard interface for Treasury contract
 * @notice Defines the core functionality interface for the Treasury contract
 */
interface ITreasury {
    // --- Asset Management ---
    /**
     * @notice Deposit WBTC from caller (Minter) to treasury
     * @dev Minter must first receive WBTC from user and approve to Treasury before calling
     * @param amt WBTC amount
     */
    function depositWBTC(uint256 amt) external;

    /**
     * @notice Withdraw WBTC from treasury to caller (Minter)
     * @dev Minter is responsible for transferring WBTC to end user
     * @param amt WBTC amount
     */
    function withdrawWBTC(uint256 amt) external;

    /**
     * @notice Compensate user with BRS tokens from treasury
     * @param to Recipient address
     * @param amt BRS amount
     */
    function compensate(address to, uint256 amt) external;

    /**
     * @notice Buyback BRS using BTD from Uniswap
     * @param btdAmount BTD amount
     * @param minBRSOut Minimum BRS output amount (slippage protection)
     */
    function buybackBRS(uint256 btdAmount, uint256 minBRSOut) external;

    // --- Configuration Management ---
    /**
     * @notice Update Uniswap Router address
     * @param newRouter New Router address
     */
    function setRouter(address newRouter) external;

    // --- Query Functions ---
    function getBalances()
        external
        view
        returns (uint256 wbtcBalance, uint256 brsBalance, uint256 btdBalance);

    function configCore() external view returns (address);
    function router() external view returns (address);

    // --- Events ---
    event WBTCDeposited(address indexed from, uint256 amount);
    event WBTCWithdrawn(address indexed to, uint256 amount);
    event BRSCompensated(address indexed to, uint256 amount);
    event BRSBuyback(uint256 btdAmount, uint256 brsReceived);
}

project/contracts/libraries/CollateralMa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/utils/math/Math.sol";
import "./Constants.sol";

/**
 * @title CollateralMath - Collateral Value and Liability Calculation Library
 * @notice Pure function library for centralized management of collateral value, liability, and quota calculations
 * @dev Provides calculation functions for core financial metrics such as collateral ratio and redeemable quota
 */
library CollateralMath {

    /**
     * @notice Calculate the USD value of collateral
     * @dev Formula: Value = (WBTC Balance × WBTC Price) / 1e8
     * @param wbtcBalance WBTC balance (8 decimals)
     * @param wbtcPrice WBTC price (18 decimals, unit: USD)
     * @return Collateral USD value (18 decimals)
     */
    function collateralValue(uint256 wbtcBalance, uint256 wbtcPrice) internal pure returns (uint256) {
        if (wbtcBalance == 0 || wbtcPrice == 0) {
            return 0;
        }
        return Math.mulDiv(wbtcBalance, wbtcPrice, Constants.PRECISION_8);
    }

    /**
     * @notice Calculate the USD value of liability
     * @dev Formula: Value = (Total BTD Equivalent × IUSD Price) / 1e18
     *      Total BTD Equivalent = BTD Supply + stBTD converted to BTD amount
     * @param btdSupply BTD total supply (18 decimals)
     * @param stBTDEquivalent stBTD converted to BTD amount (18 decimals)
     * @param iusdPrice IUSD price (18 decimals, unit: USD)
     * @return Liability USD value (18 decimals)
     */
    function liabilityValue(uint256 btdSupply, uint256 stBTDEquivalent, uint256 iusdPrice) internal pure returns (uint256) {
        uint256 totalBTD = btdSupply + stBTDEquivalent;
        if (totalBTD == 0 || iusdPrice == 0) {
            return 0;
        }
        return Math.mulDiv(totalBTD, iusdPrice, Constants.PRECISION_18);
    }

    /**
     * @notice Calculate Collateral Ratio (CR)
     * @dev Formula: CR = (Collateral Value / Liability Value) × 1e18
     *      Total BTD Equivalent = BTD Supply + stBTD converted to BTD amount
     *      - CR = 1e18 (100%) indicates fully collateralized
     *      - CR > 1e18 indicates over-collateralized
     *      - CR < 1e18 indicates under-collateralized
     *      - Returns 1e18 when position is empty
     * @param wbtcBalance WBTC balance (8 decimals)
     * @param wbtcPrice WBTC price (18 decimals)
     * @param btdSupply BTD total supply (18 decimals)
     * @param stBTDEquivalent stBTD converted to BTD amount (18 decimals)
     * @param iusdPrice IUSD price (18 decimals)
     * @return Collateral ratio (18 decimals, 1e18 = 100%)
     */
    function collateralRatio(
        uint256 wbtcBalance,
        uint256 wbtcPrice,
        uint256 btdSupply,
        uint256 stBTDEquivalent,
        uint256 iusdPrice
    ) internal pure returns (uint256) {
        uint256 totalBTD = btdSupply + stBTDEquivalent;
        if (wbtcBalance == 0 || totalBTD == 0) {
            return Constants.PRECISION_18;
        }

        uint256 colValue = collateralValue(wbtcBalance, wbtcPrice);
        uint256 liabValue = liabilityValue(btdSupply, stBTDEquivalent, iusdPrice);
        require(colValue >= Constants.MIN_USD_VALUE, "Collateral value too small");
        require(liabValue >= Constants.MIN_USD_VALUE, "Liability value too small");
        return Math.mulDiv(colValue, Constants.PRECISION_18, liabValue);
    }

    /**
     * @notice Calculate maximum redeemable USD value
     * @dev Formula: Max Redeemable = max(Collateral Value - Liability Value, 0)
     *      - Only has redemption capacity when over-collateralized
     *      - Returns 0 when under-collateralized
     * @param collateralValue_ Collateral USD value (18 decimals)
     * @param liabilityValue_ Liability USD value (18 decimals)
     * @return Maximum redeemable USD value (18 decimals)
     */
    function maxRedeemableUSD(uint256 collateralValue_, uint256 liabilityValue_) internal pure returns (uint256) {
        return collateralValue_ > liabilityValue_ ? collateralValue_ - liabilityValue_ : 0;
    }

    /**
     * @notice Calculate maximum redeemable BTD amount
     * @dev Formula: Max Redeemable BTD = (Max Redeemable USD × 1e18) / IUSD Price
     *      - First calculate USD value redemption capacity
     *      - Then convert to BTD amount
     * @param collateralValue_ Collateral USD value (18 decimals)
     * @param liabilityValue_ Liability USD value (18 decimals)
     * @param iusdPrice IUSD price (18 decimals)
     * @return Maximum redeemable BTD amount (18 decimals)
     */
    function maxRedeemableBTD(
        uint256 collateralValue_,
        uint256 liabilityValue_,
        uint256 iusdPrice
    ) internal pure returns (uint256) {
        uint256 usd = maxRedeemableUSD(collateralValue_, liabilityValue_);
        if (usd == 0 || iusdPrice == 0) {
            return 0;
        }
        return Math.mulDiv(usd, Constants.PRECISION_18, iusdPrice);
    }
}

project/contracts/libraries/Constants.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title Constants - BRS System Global Constants Library
 * @notice Centralized management of system-level immutable constants
 * @dev Uses library + internal constant to ensure compile-time inlining (zero gas overhead)
 *
 * Key features:
 * - internal constant is inlined by the compiler into contracts that use it
 * - Gas cost = 16 gas (same as local constant)
 * - Unified management, single source of truth
 * - Type-safe, compile-time checking
 */
library Constants {
    // ============ Precision Constants ============

    /// @notice 18 decimals precision (standard ERC20, USD prices)
    uint256 internal constant PRECISION_18 = 1e18;

    /// @notice 8 decimals precision (BTC)
    uint256 internal constant PRECISION_8 = 1e8;

    /// @notice 6 decimals precision (USDC/USDT)
    uint256 internal constant PRECISION_6 = 1e6;

    // ============ Precision Conversion Scale Constants ============

    /// @notice WBTC (8 decimals) to normalized (18 decimals) scale factor
    /// @dev 10^(18-8) = 1e10, used for explicit precision conversion, avoiding runtime EXP calculation
    uint256 internal constant SCALE_WBTC_TO_NORM = 1e10;

    /// @notice USDC (6 decimals) to normalized (18 decimals) scale factor
    /// @dev 10^(18-6) = 1e12, used for explicit precision conversion
    uint256 internal constant SCALE_USDC_TO_NORM = 1e12;

    /// @notice USDT (6 decimals) to normalized (18 decimals) scale factor
    /// @dev 10^(18-6) = 1e12, used for explicit precision conversion (same as USDC)
    uint256 internal constant SCALE_USDT_TO_NORM = 1e12;

    /// @notice Normalized (18 decimals) to WBTC (8 decimals) scale factor
    /// @dev 1e8, used for output precision conversion
    uint256 internal constant SCALE_NORM_TO_WBTC = 1e8;

    /// @notice Normalized (18 decimals) to USDC (6 decimals) scale factor
    /// @dev 1e6, used for output precision conversion
    uint256 internal constant SCALE_NORM_TO_USDC = 1e6;

    /// @notice Normalized (18 decimals) to USDT (6 decimals) scale factor
    /// @dev 1e6, used for output precision conversion (same as USDC)
    uint256 internal constant SCALE_NORM_TO_USDT = 1e6;

    // ============ Input Parameter Safety Limits ============

    /// @notice Maximum input parameter value (overflow prevention)
    /// @dev 1e33, two such values multiplied equals 1e66 < type(uint256).max (1.15e77)
    /// With this design, most multiplication operations don't need safeMulDiv
    uint256 internal constant MAX_INPUT_AMOUNT = 1e33;

    // ============ Minimum Operation Amount Constants ============

    /// @notice Minimum BTC operation amount per transaction (8 decimals)
    /// @dev 1 satoshi, prevents dust attacks
    uint256 internal constant MIN_BTC_AMOUNT = 1;

    /// @notice Minimum ETH operation amount per transaction (18 decimals)
    /// @dev 1e-8 ETH = 1e10 wei (0.00000001 ETH)
    uint256 internal constant MIN_ETH_AMOUNT = 1e10;

        /// @notice Minimum USD value for operations (18 decimals)
    /// @dev Used in Minter, Treasury, and other scenarios involving USD value conversion
    /// $0.001 USD, prevents dust attacks and precision loss
    ///
    /// Use cases:
    /// - Minter.mintBTD(): Check mint USD value >= $0.001
    /// - Minter.redeemBTD(): Check redeem USD value >= $0.001
    /// - Treasury.buyback(): Check buyback USD value >= $0.001
    uint256 internal constant MIN_USD_VALUE = 1e15;

    /// @notice Minimum operation amount for 6-decimal stablecoins
    /// @dev 0.001 USDC/USDT = 1000 units (6 decimals), prevents dust attacks
    /// Applicable to: USDC, USDT
    uint256 internal constant MIN_STABLECOIN_6_AMOUNT = 1000;

    /// @notice Minimum operation amount for 18-decimal stablecoins
    /// @dev 0.001 tokens = 1e15 (18 decimals), prevents dust attacks
    /// Applicable to: BTD, BTB, stBTD, stBTB
    ///
    /// Use cases:
    /// - InterestPool.stake(): Minimum stake amount for BTD/BTB
    /// - StakingRouter: Minimum stake amount for stBTD/stBTB
    uint256 internal constant MIN_STABLECOIN_18_AMOUNT = 1e15;

    // ============ Maximum Single Operation Limits (Prevents Hacker Attacks and Overflow) ============

    /// @notice Maximum WBTC amount per single operation
    /// @dev 10,000 BTC (8 decimals = 10000 * 1e8)
    /// Prevents hacker attacks and integer overflow, applies to all WBTC transfer/mint/redeem operations
    uint256 internal constant MAX_WBTC_AMOUNT = 10_000 * 1e8;

    /// @notice Maximum ETH amount per single operation
    /// @dev 100,000 ETH (18 decimals = 100000 * 1e18)
    /// Prevents hacker attacks and integer overflow
    uint256 internal constant MAX_ETH_AMOUNT = 100_000 * 1e18;

    /// @notice Maximum 6-decimal stablecoin amount per single operation
    /// @dev 1 billion USDC/USDT (6 decimals = 1000000000 * 1e6)
    /// Prevents hacker attacks and integer overflow
    /// Applicable to: USDC, USDT
    uint256 internal constant MAX_STABLECOIN_6_AMOUNT = 1_000_000_000 * 1e6;

    /// @notice Maximum 18-decimal stablecoin amount per single operation
    /// @dev 1 billion BTD/BTB/stBTD/stBTB (18 decimals = 1000000000 * 1e18)
    /// Prevents hacker attacks and integer overflow
    /// Applicable to: BTD, BTB, stBTD, stBTB and other 18-decimal stablecoins
    uint256 internal constant MAX_STABLECOIN_18_AMOUNT = 1_000_000_000 * 1e18;

    /// @notice Maximum USD value per single operation
    /// @dev Equivalent to 1 billion USD (18 decimals = 1000000000 * 1e18)
    /// Prevents hacker attacks and integer overflow, applies to all USD value calculations
    uint256 internal constant MAX_USD_VALUE = 1_000_000_000 * 1e18;

    /// @notice Percentage base (100%)
    uint256 internal constant PERCENT_BASE = 100;

    /// @notice Basis points base (10000 = 100.00%)
    uint256 internal constant BPS_BASE = 10000;

    // ============ Time Constants ============

    /// @notice Seconds per year
    uint256 internal constant SECONDS_PER_YEAR = 365 days;

    /// @notice Seconds per day
    uint256 internal constant SECONDS_PER_DAY = 1 days;

    /// @notice Seconds per week
    uint256 internal constant SECONDS_PER_WEEK = 7 days;

    /// @notice BTC halving cycle (4 years)
    uint256 internal constant ERA_PERIOD = 4 * 365 days;

    // ============ Inflation Parameter Constants ============

    /// @notice Fixed annual inflation rate - 2%
    /// @dev Fixed inflation target specified in whitepaper, used for IUSD (Ideal USD) calculation
    /// 2% = 0.02 = 2e16 (18 decimals)
    uint256 internal constant ANNUAL_INFLATION_RATE = 2e16;

    /// @notice Monthly growth factor - (1.02)^(1/12)
    /// @dev Calculated from 2% annual inflation
    /// Formula: (1 + 0.02)^(1/12) = 1.001651581301920174
    /// Calculated off-chain with high precision and stored with 18 decimals
    uint256 internal constant MONTHLY_GROWTH_FACTOR = 1001651581301920174;

    // ============ Supply Constants ============

    /// @notice BRS maximum supply (2.1 billion, tribute to BTC's 21 million)
    /// @dev Maximum supply of BRS tokens, the only definition in the system
    uint256 internal constant BRS_MAX_SUPPLY = 2_100_000_000e18;
}

