project/contracts/ConfigCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title ConfigCore
 * @notice Immutable configuration core - stores critical system addresses
 * @dev Cannot be changed after deployment, ensuring system core architecture stability
 * @dev All core addresses are set once via constructor and permanently fixed
 */
contract ConfigCore {
    // ==================== Core Token Addresses (immutable) ====================

    /// @notice WBTC token address - used for collateral to mint BTD
    /// @dev Cannot be changed after deployment
    address public immutable WBTC;

    /// @notice BTD stablecoin address - primary stablecoin
    /// @dev Cannot be changed after deployment
    address public immutable BTD;

    /// @notice BTB bond token address - redemption bonds
    /// @dev Cannot be changed after deployment
    address public immutable BTB;

    /// @notice BRS governance token address - governance token
    /// @dev Cannot be changed after deployment
    address public immutable BRS;

    /// @notice WETH token address - Wrapped ETH
    /// @dev Cannot be changed after deployment
    address public immutable WETH;

    /// @notice USDC token address - stablecoin reserve
    /// @dev Cannot be changed after deployment
    address public immutable USDC;

    /// @notice USDT token address - stablecoin reserve
    /// @dev Cannot be changed after deployment
    address public immutable USDT;

    // ==================== Core Contract Addresses (storage - deferred binding) ====================
    // These 5 contracts have circular dependencies with ConfigCore, so they use storage and are set via setCoreContracts()

    /// @notice Treasury contract address - manages system assets
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public TREASURY;

    /// @notice Minter contract address - handles BTD minting and redemption
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public MINTER;

    /// @notice Price oracle address - provides WBTC price data
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public PRICE_ORACLE;

    /// @notice IUSD manager address - manages Ideal USD inflation adjustments
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public IDEAL_USD_MANAGER;

    /// @notice Interest pool address - manages BTD and BTB interest distribution
    /// @dev Set once via setCoreContracts() and cannot be changed afterward
    address public INTEREST_POOL;

    /// @notice Flag indicating whether core contracts have been set
    /// @dev Ensures setCoreContracts() can only be called once
    bool public coreContractsSet;

    /// @notice Flag indicating whether peripheral contracts have been set
    /// @dev Ensures setPeripheralContracts() can only be called once
    bool public peripheralContractsSet;

    // ==================== Price Oracle Data Source Addresses (immutable) ====================

    /// @notice Chainlink BTC/USD price feed address
    /// @dev Cannot be changed after deployment
    address public immutable CHAINLINK_BTC_USD;

    /// @notice Chainlink WBTC/BTC price feed address
    /// @dev Cannot be changed after deployment
    address public immutable CHAINLINK_WBTC_BTC;

    /// @notice Pyth WBTC price feed address
    /// @dev Cannot be changed after deployment
    address public immutable PYTH_WBTC;

    /// @notice Redstone WBTC price feed address
    /// @dev Cannot be changed after deployment
    address public immutable REDSTONE_WBTC;

    // ==================== Core Pool Addresses (deferred binding) ====================

    /// @notice Staking router address - handles staking operation routing
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public STAKING_ROUTER;

    /// @notice Farming pool address - BRS liquidity mining
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public FARMING_POOL;

    /// @notice stBTD token address - BTD staking receipt
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public ST_BTD;

    /// @notice stBTB token address - BTB staking receipt
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public ST_BTB;

    /// @notice Governor contract address - DAO governance
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public GOVERNOR;

    /// @notice TWAP oracle address - time-weighted average price oracle
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public TWAP_ORACLE;

    // ==================== Uniswap V2 Pool Addresses (deferred binding) ====================

    /// @notice WBTC-USDC Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_WBTC_USDC;

    /// @notice BTD-USDC Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_BTD_USDC;

    /// @notice BTB-BTD Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_BTB_BTD;

    /// @notice BRS-BTD Uniswap V2 pair address
    /// @dev Set once via setPeripheralContracts() and cannot be changed afterward
    address public POOL_BRS_BTD;

    /**
     * @notice Constructor - sets all non-circular-dependency addresses
     * @dev The 5 core contracts with circular dependencies are set separately via setCoreContracts()
     */
    constructor(
        address _wbtc,                 // WBTC token address
        address _btd,                  // BTD stablecoin address
        address _btb,                  // BTB bond token address
        address _brs,                  // BRS governance token address
        address _weth,                 // WETH token address
        address _usdc,                 // USDC token address
        address _usdt,                 // USDT token address
        address _chainlinkBtcUsd,      // Chainlink BTC/USD price feed address
        address _chainlinkWbtcBtc,     // Chainlink WBTC/BTC price feed address
        address _pythWbtc,             // Pyth WBTC price feed address
        address _redstoneWbtc          // Redstone WBTC price feed address
    ) {
        require(_wbtc != address(0), "Invalid WBTC");
        require(_btd != address(0), "Invalid BTD");
        require(_btb != address(0), "Invalid BTB");
        require(_brs != address(0), "Invalid BRS");
        require(_weth != address(0), "Invalid WETH");
        require(_usdc != address(0), "Invalid USDC");
        require(_usdt != address(0), "Invalid USDT");
        require(_chainlinkBtcUsd != address(0), "Invalid Chainlink BTC/USD");
        require(_chainlinkWbtcBtc != address(0), "Invalid Chainlink WBTC/BTC");
        require(_pythWbtc != address(0), "Invalid Pyth WBTC");
        require(_redstoneWbtc != address(0), "Invalid Redstone WBTC");

        WBTC = _wbtc;
        BTD = _btd;
        BTB = _btb;
        BRS = _brs;
        WETH = _weth;
        USDC = _usdc;
        USDT = _usdt;
        CHAINLINK_BTC_USD = _chainlinkBtcUsd;
        CHAINLINK_WBTC_BTC = _chainlinkWbtcBtc;
        PYTH_WBTC = _pythWbtc;
        REDSTONE_WBTC = _redstoneWbtc;
    }

    /**
     * @notice Sets the 5 core contract addresses with circular dependencies
     * @dev Can only be called once, permanently locked after deployment
     * @param _treasury Treasury contract address
     * @param _minter Minter contract address
     * @param _priceOracle Price oracle address
     * @param _idealUSDManager IUSD manager address
     * @param _interestPool Interest pool address
     */
    function setCoreContracts(
        address _treasury,
        address _minter,
        address _priceOracle,
        address _idealUSDManager,
        address _interestPool
    ) external {
        require(!coreContractsSet, "Core contracts already set");
        require(_treasury != address(0), "Invalid Treasury");
        require(_minter != address(0), "Invalid Minter");
        require(_priceOracle != address(0), "Invalid PriceOracle");
        require(_idealUSDManager != address(0), "Invalid IdealUSDManager");
        require(_interestPool != address(0), "Invalid InterestPool");

        TREASURY = _treasury;
        MINTER = _minter;
        PRICE_ORACLE = _priceOracle;
        IDEAL_USD_MANAGER = _idealUSDManager;
        INTEREST_POOL = _interestPool;
        coreContractsSet = true;
    }

    /**
     * @notice Sets peripheral contracts and pools with circular dependencies
     * @dev Can only be called once, permanently locked after deployment
     */
    function setPeripheralContracts(
        address _stakingRouter,
        address _farmingPool,
        address _stBTD,
        address _stBTB,
        address _governor,
        address _twapOracle,
        address _poolWbtcUsdc,
        address _poolBtdUsdc,
        address _poolBtbBtd,
        address _poolBrsBtd
    ) external {
        require(!peripheralContractsSet, "Peripheral contracts already set");
        require(_stakingRouter != address(0), "Invalid StakingRouter");
        require(_farmingPool != address(0), "Invalid FarmingPool");
        require(_stBTD != address(0), "Invalid stBTD");
        require(_stBTB != address(0), "Invalid stBTB");
        require(_governor != address(0), "Invalid Governor");
        require(_twapOracle != address(0), "Invalid TWAPOracle");
        require(_poolWbtcUsdc != address(0), "Invalid Pool WBTC/USDC");
        require(_poolBtdUsdc != address(0), "Invalid Pool BTD/USDC");
        require(_poolBtbBtd != address(0), "Invalid Pool BTB/BTD");
        require(_poolBrsBtd != address(0), "Invalid Pool BRS/BTD");

        STAKING_ROUTER = _stakingRouter;
        FARMING_POOL = _farmingPool;
        ST_BTD = _stBTD;
        ST_BTB = _stBTB;
        GOVERNOR = _governor;
        TWAP_ORACLE = _twapOracle;
        POOL_WBTC_USDC = _poolWbtcUsdc;
        POOL_BTD_USDC = _poolBtdUsdc;
        POOL_BTB_BTD = _poolBtbBtd;
        POOL_BRS_BTD = _poolBrsBtd;
        peripheralContractsSet = true;
    }
}

project/contracts/ConfigGov.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/Constants.sol";

/**
 * @title ConfigGov - Governable Parameter Configuration Contract
 * @notice Manages runtime-adjustable system parameters (fees, limits, oracle addresses, etc.)
 * @dev Separated from ConfigCore: Core manages immutable addresses, Gov manages mutable parameters
 *      Governance logic can be upgraded without affecting core architecture
 */
contract ConfigGov is Ownable {

    /**
     * @notice Parameter type enum (uint256 type)
     * @dev Used for unified management of all system parameters, supports unlimited extension
     */
    enum ParamType {
        MINT_FEE_BP,        // 0 - Minting fee rate (basis points)
        INTEREST_FEE_BP,    // 1 - Interest fee rate (basis points)
        MIN_BTB_PRICE,      // 2 - BTB minimum price (18 decimals)
        MAX_BTB_RATE,       // 3 - BTB maximum interest rate (18 decimals)
        PCE_MAX_DEVIATION,  // 4 - PCE maximum deviation rate (18 decimals, e.g., 2e16 = 2%)
        REDEEM_FEE_BP       // 5 - Redemption fee rate (basis points)
        // Future extensions: LIQUIDATION_FEE, STABILITY_FEE, etc.
    }

    /**
     * @notice Address parameter type enum
     * @dev Used for managing governable oracle addresses, external contract addresses, etc.
     */
    enum AddressParamType {
        PCE_FEED          // 0 - Chainlink PCE oracle address
        // Future extensions: BACKUP_PCE_FEED, CPI_FEED, etc.
    }

    // ============ Storage ============

    /// @notice Unified parameter registry (governable fees, limits, and other parameters)
    mapping(ParamType => uint256) private _params;

    /// @notice Address parameter registry (governable oracle addresses, etc.)
    mapping(AddressParamType => address) private _addressParams;

    // ============ Events ============

    /// @notice Generic parameter update event (uint256 type)
    event ParamUpdated(ParamType indexed paramType, uint256 newValue);

    /// @notice Address parameter update event
    event AddressParamUpdated(AddressParamType indexed paramType, address newValue);

    // ============ Initialization ============

    /**
     * @notice Constructor - initializes governance parameters
     * @param initialOwner Contract owner address
     */
    constructor(
        address initialOwner
    ) Ownable(initialOwner) {
        require(initialOwner != address(0), "ConfigGov: zero owner");
    }

    // ============ Parameter Management ============

    /**
     * @notice Sets a single system parameter
     * @dev Only owner can call, used for updating fees, price limits, and other parameters
     * @param paramType Parameter type (from ParamType enum)
     * @param value New parameter value
     */
    function setParam(ParamType paramType, uint256 value) external onlyOwner {
        // Parameter range validation
        _validateParam(paramType, value);

        _params[paramType] = value;
        emit ParamUpdated(paramType, value);
    }

    /**
     * @notice Validates parameter range
     * @dev Internal function to ensure parameters are within reasonable bounds
     * @param paramType Parameter type
     * @param value Parameter value
     */
    function _validateParam(ParamType paramType, uint256 value) private pure {
        if (paramType == ParamType.MINT_FEE_BP) {
            require(value >= 1, "ConfigGov: mint fee too low");  // Minimum 0.01% (1 basis point)
            require(value <= 1000, "ConfigGov: mint fee too high"); // Maximum 10%
        } else if (paramType == ParamType.INTEREST_FEE_BP) {
            require(value >= 1, "ConfigGov: interest fee too low");
            require(value <= 1000, "ConfigGov: interest fee too high");
        } else if (paramType == ParamType.REDEEM_FEE_BP) {
            require(value >= 1, "ConfigGov: redeem fee too low");
            require(value <= 1000, "ConfigGov: redeem fee too high");
        } else if (paramType == ParamType.MIN_BTB_PRICE) {
            require(value >= 1e17, "ConfigGov: min BTB price too low"); // Minimum 0.1 BTD
            require(value <= 2e18, "ConfigGov: min BTB price too high"); // Maximum 2 BTD
        } else if (paramType == ParamType.MAX_BTB_RATE) {
            require(value >= 1e16, "ConfigGov: max BTB rate too low"); // Minimum 1% APR
            require(value <= 2e17, "ConfigGov: max BTB rate too high"); // Maximum 20% APR
        } else if (paramType == ParamType.PCE_MAX_DEVIATION) {
            require(value >= 1e15, "ConfigGov: PCE deviation too low"); // Minimum 0.1%
            require(value <= 1e17, "ConfigGov: PCE deviation too high"); // Maximum 10%
        }
    }

    /**
     * @notice Batch sets multiple system parameters
     * @dev Only owner can call, array lengths must be equal
     * @param paramTypes Parameter type array
     * @param values Corresponding new parameter value array
     */
    function setParamsBatch(
        ParamType[] calldata paramTypes,
        uint256[] calldata values
    ) external onlyOwner {
        require(paramTypes.length == values.length, "ConfigGov: length mismatch");
        for (uint i = 0; i < paramTypes.length; i++) {
            // Parameter range validation
            _validateParam(paramTypes[i], values[i]);

            _params[paramTypes[i]] = values[i];
            emit ParamUpdated(paramTypes[i], values[i]);
        }
    }

    /**
     * @notice Gets the value of a specified system parameter type
     * @dev Returns 0 if the parameter is not set
     * @param paramType Parameter type
     * @return Parameter value
     */
    function getParam(ParamType paramType) external view returns (uint256) {
        return _params[paramType];
    }

    // ============ Convenience Access Functions ============

    /**
     * @notice Gets the minting fee rate
     * @return Minting fee rate (basis points, e.g., 50 = 0.5%)
     */
    function mintFeeBP() external view returns (uint256) {
        return _params[ParamType.MINT_FEE_BP];
    }

    function interestFeeBP() external view returns (uint256) {
        return _params[ParamType.INTEREST_FEE_BP];
    }

    function minBTBPrice() external view returns (uint256) {
        return _params[ParamType.MIN_BTB_PRICE];
    }

    function maxBTBRate() external view returns (uint256) {
        return _params[ParamType.MAX_BTB_RATE];
    }

    /**
     * @notice Gets PCE maximum deviation rate
     * @dev Used to prevent abnormal PCE data fluctuations
     * @return PCE maximum deviation rate (18 decimals, e.g., 2e16 = 2%)
     */
    function pceMaxDeviation() external view returns (uint256) {
        return _params[ParamType.PCE_MAX_DEVIATION];
    }

    /**
     * @notice Gets the redemption fee rate
     * @dev Fee deducted from user when redeeming BTD
     * @return Redemption fee rate (basis points, e.g., 50 = 0.5%)
     */
    function redeemFeeBP() external view returns (uint256) {
        return _params[ParamType.REDEEM_FEE_BP];
    }

    // ============ Address Parameter Management ============

    /**
     * @notice Sets a single address parameter
     * @dev Only owner can call, used for updating oracle addresses, etc.
     * @param paramType Address parameter type (from AddressParamType enum)
     * @param value New address value
     */
    function setAddressParam(AddressParamType paramType, address value) external onlyOwner {
        require(value != address(0), "ConfigGov: zero address");
        _addressParams[paramType] = value;
        emit AddressParamUpdated(paramType, value);
    }

    /**
     * @notice Batch sets multiple address parameters
     * @dev Only owner can call, array lengths must be equal
     * @param paramTypes Address parameter type array
     * @param values Corresponding new address value array
     */
    function setAddressParamsBatch(
        AddressParamType[] calldata paramTypes,
        address[] calldata values
    ) external onlyOwner {
        require(paramTypes.length == values.length, "ConfigGov: length mismatch");
        for (uint i = 0; i < paramTypes.length; i++) {
            require(values[i] != address(0), "ConfigGov: zero address");
            _addressParams[paramTypes[i]] = values[i];
            emit AddressParamUpdated(paramTypes[i], values[i]);
        }
    }

    /**
     * @notice Gets the value of a specified address parameter type
     * @dev Returns address(0) if the parameter is not set
     * @param paramType Address parameter type
     * @return Address value
     */
    function getAddressParam(AddressParamType paramType) external view returns (address) {
        return _addressParams[paramType];
    }

    /**
     * @notice Gets PCE Feed oracle address
     * @return PCE Feed address
     */
    function pceFeed() external view returns (address) {
        return _addressParams[AddressParamType.PCE_FEED];
    }
}

project/contracts/InterestPool.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.4.0
pragma solidity ^0.8.30;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC4626} from "@openzeppelin/contracts/interfaces/IERC4626.sol";
import "./ConfigCore.sol";
import "./ConfigGov.sol";
import {IMintableERC20} from "./interfaces/IMintableERC20.sol";
import {IPriceOracle} from "./interfaces/IPriceOracle.sol";
import {IInterestPool} from "./interfaces/IInterestPool.sol";
import "./libraries/Constants.sol";
import "./libraries/InterestMath.sol";

/// @title InterestPool
/// @notice Manages interest accrual for BTD and BTB deposited via stBTD/stBTB vaults.
///         Users cannot interact with this contract directly - they must use stBTD/stBTB vault contracts.
///         BTD APR dynamically adjusts based on Collateral Ratio (CR):
///         - CR 90%-120%: 4% (stable zone)
///         - CR 30%-90%: 4%-20% (risk zone, stepwise increase)
///         - CR 120%-200%: 4%-0% (over-collateralized, stepwise decrease)
///         BTB APR is adjusted daily based on the BTB/BTD Uniswap price and constrained by Config.maxBTBRate().
/// @dev Only stBTD and stBTB vault contracts are authorized to call deposit/withdraw functions.
contract InterestPool is Ownable, ReentrancyGuard, IInterestPool {
    using SafeERC20 for IMintableERC20;

    uint256 private constant LOWER_PRICE_THRESHOLD = (Constants.PRECISION_18 * 99) / 100; // 0.99 BTD
    uint256 private constant UPPER_PRICE_THRESHOLD = (Constants.PRECISION_18 * 101) / 100; // 1.01 BTD

    // BTD rate adjustment thresholds (CR-based)
    uint256 private constant CR_MIN = (Constants.PRECISION_18 * 30) / 100;  // 30%
    uint256 private constant CR_LOW = (Constants.PRECISION_18 * 90) / 100;  // 90%
    uint256 private constant CR_HIGH = (Constants.PRECISION_18 * 120) / 100; // 120%
    uint256 private constant CR_MAX = (Constants.PRECISION_18 * 200) / 100; // 200%

    uint256 private constant BTD_BASE_RATE = 400;   // 4% in bps
    uint256 private constant BTD_MAX_RATE = 2000;   // 20% in bps
    uint256 private constant BTD_MIN_RATE = 0;      // 0% in bps

    ConfigCore public immutable core;
    ConfigGov public gov;
    address public rateOracle; // Oracle address allowed to update rates

    struct Pool {
        IMintableERC20 token;
        uint256 totalStaked;
        uint256 accInterestPerShare; // Accumulated interest per token, scaled by 1e18
        uint256 lastAccrual;
        uint256 annualRateBps; // Current APR in basis points
    }

    struct UserInfo {
        uint256 amount; // Staked principal
        uint256 rewardDebt; // Tracks distributed interest
    }

    Pool public btdPool;
    Pool public btbPool;

    mapping(address => UserInfo) private btdUsers;
    mapping(address => UserInfo) private btbUsers;

    uint256 public btbLastPrice; // Last recorded BTB price in BTD (18 decimals)
    uint256 public btbLastRateUpdate; // Timestamp of the last BTB rate adjustment

    event RateOracleUpdated(address indexed newOracle);
    event TreasuryFeeMinted(address indexed token, uint256 amount);

    modifier onlyRateOracle() {
        require(msg.sender == rateOracle, "InterestPool: only rate oracle");
        _;
    }

    constructor(
        address initialOwner,
        address _core,
        address _gov,
        address _rateOracle
    ) Ownable(initialOwner) {
        require(initialOwner != address(0), "InterestPool: invalid owner");
        require(_core != address(0), "InterestPool: core zero");
        require(_gov != address(0), "InterestPool: gov zero");
        core = ConfigCore(_core);
        gov = ConfigGov(_gov);

        address btdAddress = core.BTD();
        address btbAddress = core.BTB();
        require(btdAddress != address(0) && btbAddress != address(0), "Token addresses not set");

        btdPool = Pool({
            token: IMintableERC20(btdAddress),
            totalStaked: 0,
            accInterestPerShare: 0,
            lastAccrual: block.timestamp,
            annualRateBps: BTD_BASE_RATE  // BTD and BTB use same initial rate
        });

        btbPool = Pool({
            token: IMintableERC20(btbAddress),
            totalStaked: 0,
            accInterestPerShare: 0,
            lastAccrual: block.timestamp,
            annualRateBps: BTD_BASE_RATE  // BTB rate is later dynamically adjusted by market mechanism
        });

    }

    // --- User-facing staking functions removed ---
    // Users should interact via stBTD/stBTB vault contracts instead of directly with InterestPool

    /// @notice Gets current BTB price
    /// @dev Reads TWAP-protected BTB price from PriceOracle
    /// @return BTB price in BTD terms, precision 1e18
    function getBTBPrice() external view returns (uint256) {
        return _currentBTBPrice();
    }

    // --- Rate management ---

    /// @notice Upgrades governance contract
    /// @dev Only contract owner can call, core addresses in ConfigCore cannot be changed
    /// @param newGov New ConfigGov contract address
    function upgradeGov(address newGov) external onlyOwner {
        require(newGov != address(0), "Invalid gov");
        gov = ConfigGov(newGov);
    }

    /// @notice Sets rate oracle address
    /// @dev Only contract owner can call
    /// @param newOracle New rate oracle address
    function setRateOracle(address newOracle) external onlyOwner {
        require(newOracle != address(0), "InterestPool: zero oracle");
        rateOracle = newOracle;
        emit RateOracleUpdated(newOracle);
    }

    /// @notice Sets Federal Funds Rate oracle address
    /// @dev Only contract owner can call
    /// @param _ffrOracle FFR oracle contract address


    /// @notice Dynamically updates BTD annual rate (APR) based on CR
    /// @dev Stepwise adjustment logic:
    ///      - CR 90%-120%: maintain 4% (stable zone)
    ///      - CR 30%-90%: stepwise increase 4%-20% (risk zone, incentivize redemption)
    ///      - CR 120%-200%: stepwise decrease 4%-0% (over-collateralized zone, incentivize minting)
    ///      Only rate oracle or contract owner can call
    function updateBTDAnnualRate() external onlyRateOracle {
        _accruePool(btdPool);

        // Get current CR
        uint256 cr = _getCurrentCR();
        uint256 oldRate = btdPool.annualRateBps;
        uint256 newRate = _calculateBTDRateByCR(cr);

        btdPool.annualRateBps = newRate;
        emit BTDAnnualRateUpdated(oldRate, newRate);
    }

    /// @notice Calculates BTD rate based on CR
    /// @dev Stepwise calculation:
    ///      CR < 30%: 20%
    ///      CR 30%-90%: linear interpolation 20% -> 4%
    ///      CR 90%-120%: 4%
    ///      CR 120%-200%: linear interpolation 4% -> 0%
    ///      CR > 200%: 0%
    function _calculateBTDRateByCR(uint256 cr) internal pure returns (uint256) {
        if (cr <= CR_MIN) {
            // CR <= 30%: maximum rate 20%
            return BTD_MAX_RATE;
        } else if (cr < CR_LOW) {
            // CR 30%-90%: linear decrease from 20% to 4%
            // rate = 20% - (cr - 30%) * (16% / 60%)
            uint256 range = CR_LOW - CR_MIN; // 60%
            uint256 rateRange = BTD_MAX_RATE - BTD_BASE_RATE; // 1600 bps
            uint256 decrease = ((cr - CR_MIN) * rateRange) / range;
            return BTD_MAX_RATE - decrease;
        } else if (cr <= CR_HIGH) {
            // CR 90%-120%: stable at 4%
            return BTD_BASE_RATE;
        } else if (cr < CR_MAX) {
            // CR 120%-200%: linear decrease from 4% to 0%
            // rate = 4% - (cr - 120%) * (4% / 80%)
            uint256 range = CR_MAX - CR_HIGH; // 80%
            uint256 rateRange = BTD_BASE_RATE - BTD_MIN_RATE; // 400 bps
            uint256 decrease = ((cr - CR_HIGH) * rateRange) / range;
            return BTD_BASE_RATE - decrease;
        } else {
            // CR >= 200%: minimum rate 0%
            return BTD_MIN_RATE;
        }
    }

    /// @notice Gets current Collateral Ratio (CR)
    /// @dev CR = (WBTC value) / (BTD equivalent total value)
    ///      BTD equivalent total = BTD supply + stBTD converted to BTD amount
    function _getCurrentCR() internal view returns (uint256) {
        IPriceOracle oracle = IPriceOracle(core.PRICE_ORACLE());
        uint256 wbtcPrice = oracle.getWBTCPrice();
        uint256 iusdPrice = oracle.getIUSDPrice();

        uint256 wbtcBalance = IERC20(core.WBTC()).balanceOf(core.TREASURY());
        uint256 btdSupply = IERC20(core.BTD()).totalSupply();

        // BTD liability = circulating BTD total supply
        // Note: stBTD is just a wrapped token of BTD, should not be double counted
        // BTD's totalSupply already includes all minted BTD (including those in stBTD vault)
        if (btdSupply == 0) {
            return Constants.PRECISION_18; // 100%, return 100% when no liability
        }

        // CR = (wbtcBalance * wbtcPrice / 1e8) / (btdSupply * iusdPrice / 1e18)
        // Optimization: multiply first then divide to avoid precision loss
        // CR = (wbtcBalance * wbtcPrice * 1e18 * 1e18) / (1e8 * btdSupply * iusdPrice)
        //    = (wbtcBalance * wbtcPrice * 1e28) / (btdSupply * iusdPrice)
        // Note: need to check if btdSupply * iusdPrice is 0
        uint256 liabilityNumerator = btdSupply * iusdPrice;
        if (liabilityNumerator == 0) {
            return Constants.PRECISION_18;
        }

        // Using 1e28 = 1e18 (CR precision) * 1e18 (iusdPrice precision) / 1e8 (wbtcPrice precision)
        return (wbtcBalance * wbtcPrice * 1e28) / liabilityNumerator;
    }

    /// @notice Updates BTB annual rate (APR)
    /// @dev Can only adjust once per day, based on BTB/BTD price dynamics:
    ///      - If price < 0.99 BTD and daily change < 0, increase APR (by |change rate|)
    ///      - If price > 1.01 BTD and daily change > 0, decrease APR (by change rate)
    ///      - Final APR is constrained to [0, Config.maxBTBRate()] range
    ///      Only rate oracle or contract owner can call
    function updateBTBAnnualRate() external onlyRateOracle {
        if (btbLastRateUpdate != 0) {
            require(block.timestamp >= btbLastRateUpdate + Constants.SECONDS_PER_DAY, "BTB rate already updated today");
        }

        _accruePool(btbPool);

        uint256 price = _currentBTBPrice();
        int256 changeBps = _calculateChangeBps(btbLastPrice, price);

        uint256 oldRate = btbPool.annualRateBps;
        uint256 newRate = oldRate;

        if (btbLastPrice != 0) {
            if (price < LOWER_PRICE_THRESHOLD && changeBps < 0) {
                uint256 delta = uint256(-changeBps);
                newRate += delta;
            } else if (price > UPPER_PRICE_THRESHOLD && changeBps > 0) {
                uint256 delta = uint256(changeBps);
                if (delta >= newRate) {
                    newRate = 0;
                } else {
                    newRate -= delta;
                }
            }
        }

        uint256 maxRate = gov.maxBTBRate();
        if (newRate > maxRate) {
            newRate = maxRate;
        }

        btbPool.annualRateBps = newRate;
        btbLastPrice = price;
        btbLastRateUpdate = block.timestamp;

        emit BTBAnnualRateUpdated(oldRate, newRate, price, changeBps);
    }

    // --- Internal staking logic ---
    // Direct user staking functions (_stake, _withdraw, _claim) removed
    // Only stToken vault interface is supported

    function _pendingCurrent(Pool storage pool, UserInfo storage user) internal view returns (uint256) {
        return InterestMath.pendingReward(user.amount, pool.accInterestPerShare, user.rewardDebt);
    }

    function _accruePool(Pool storage pool) internal {
        if (pool.lastAccrual == block.timestamp) {
            return;
        }

        if (pool.totalStaked == 0 || pool.annualRateBps == 0) {
            pool.lastAccrual = block.timestamp;
            return;
        }

        uint256 timeElapsed = block.timestamp - pool.lastAccrual;
        if (timeElapsed == 0) {
            return;
        }

        uint256 interestPerShare = InterestMath.interestPerShareDelta(pool.annualRateBps, timeElapsed);
        if (interestPerShare > 0) {
            pool.accInterestPerShare += interestPerShare;
        }

        pool.lastAccrual = block.timestamp;
    }

    function _payout(Pool storage pool, address recipient, uint256 amount) internal {
        // Mint interest to user
        pool.token.mint(recipient, amount);

        // For BTD pool, mint additional 10% fee to treasury
        if (address(pool.token) == address(btdPool.token)) {
            address treasury = core.TREASURY();
            require(treasury != address(0), "Treasury not set");
            uint256 fee = InterestMath.feeAmount(amount, 1000); // 10% = 1000 bps
            if (fee > 0) {
                pool.token.mint(treasury, fee);
                emit TreasuryFeeMinted(address(pool.token), fee);
            }
        }
    }

    // --- Price helpers ---

    /// @notice Gets BTB price from PriceOracle (TWAP protected)
    /// @dev Uses PriceOracle.getBTBPrice() for TWAP to prevent flash loan attacks
    ///      Previous version directly used Uniswap reserves (vulnerable to manipulation)
    /// @return price BTB price in USD terms, precision 1e18
    function _currentBTBPrice() internal view returns (uint256) {
        address oracleAddr = core.PRICE_ORACLE();
        require(oracleAddr != address(0), "PriceOracle not set");

        // Get TWAP-protected price from PriceOracle
        // PriceOracle.getBTBPrice() uses 30-minute TWAP when enabled (production)
        // or spot price when TWAP is disabled (testing only)
        uint256 btbPriceUSD = IPriceOracle(oracleAddr).getBTBPrice();

        // Convert from USD to BTD terms (assuming BTD ≈ $1)
        // Both are in 18 decimals, so direct return
        return btbPriceUSD;
    }

    function _calculateChangeBps(uint256 previousPrice, uint256 currentPrice) internal pure returns (int256) {
        return InterestMath.priceChangeBps(previousPrice, currentPrice);
    }

    // --- Virtual Staking Interface (for Coinbase integration) ---

    /// @notice Gets total staked amount for a specific token (for virtual pool integration)
    /// @dev Used for virtual pool integration with platforms like Coinbase
    /// @param token Token address to query
    /// @return Total staked amount, precision 1e18
    function totalStaked(address token) external view returns (uint256) {
        if (token == address(btdPool.token)) {
            return btdPool.totalStaked;
        } else if (token == address(btbPool.token)) {
            return btbPool.totalStaked;
        }
        return 0;
    }

    /// @notice Gets user's staked amount in a specific token pool (for virtual pool integration)
    /// @dev Used for virtual pool integration with platforms like Coinbase
    /// @param token Token address to query
    /// @param user User address to query
    /// @return User's staked amount, precision 1e18
    function userStaked(address token, address user) external view returns (uint256) {
        if (token == address(btdPool.token)) {
            return btdUsers[user].amount;
        } else if (token == address(btbPool.token)) {
            return btbUsers[user].amount;
        }
        return 0;
    }

    // --- stToken Vault Interface ---


    /// @notice Stakes BTD to interest pool
    /// @dev Anyone can call, automatically accrues interest and pays out pending interest
    /// @param amount BTD amount to stake, precision 1e18, must be >= minimum stake amount
    function stakeBTD(uint256 amount) external nonReentrant {
        require(amount >= Constants.MIN_STABLECOIN_18_AMOUNT, "Stake amount too small");
        require(amount <= Constants.MAX_STABLECOIN_18_AMOUNT, "Stake amount too large");

        _accruePool(btdPool);

        UserInfo storage user = btdUsers[msg.sender];
        uint256 pendingAmount = _pendingCurrent(btdPool, user);

        if (pendingAmount > 0) {
            _payout(btdPool, msg.sender, pendingAmount);
            emit InterestClaimed(msg.sender, address(btdPool.token), pendingAmount);
        }

        btdPool.token.safeTransferFrom(msg.sender, address(this), amount);
        user.amount += amount;
        btdPool.totalStaked += amount;

        user.rewardDebt = InterestMath.rewardDebtValue(user.amount, btdPool.accInterestPerShare);
        emit Staked(msg.sender, address(btdPool.token), amount);
    }

    /// @notice Stakes BTB to interest pool
    /// @dev Anyone can call, automatically accrues interest and pays out pending interest
    /// @param amount BTB amount to stake, precision 1e18, must be >= minimum stake amount
    function stakeBTB(uint256 amount) external nonReentrant {
        require(amount >= Constants.MIN_STABLECOIN_18_AMOUNT, "Stake amount too small");
        require(amount <= Constants.MAX_STABLECOIN_18_AMOUNT, "Stake amount too large");

        _accruePool(btbPool);

        UserInfo storage user = btbUsers[msg.sender];
        uint256 pendingAmount = _pendingCurrent(btbPool, user);

        // CEI Pattern: Effects before Interactions
        // Update state variables BEFORE external calls to prevent reentrancy
        btbPool.token.safeTransferFrom(msg.sender, address(this), amount);
        user.amount += amount;
        btbPool.totalStaked += amount;
        user.rewardDebt = InterestMath.rewardDebtValue(user.amount, btbPool.accInterestPerShare);

        // Interactions: External calls after state updates
        if (pendingAmount > 0) {
            _payout(btbPool, msg.sender, pendingAmount);
            emit InterestClaimed(msg.sender, address(btbPool.token), pendingAmount);
        }

        emit Staked(msg.sender, address(btbPool.token), amount);
    }

    /// @notice Unstakes BTD
    /// @dev Anyone can call, withdrawal amount can include pending interest
    ///      Proportionally allocates interest and principal, interest portion is minted, principal portion is transferred from contract balance
    /// @param amount BTD amount to unstake (can include pending interest), precision 1e18
    function unstakeBTD(uint256 amount) external nonReentrant {
        require(amount >= Constants.MIN_STABLECOIN_18_AMOUNT, "Unstake amount too small");
        require(amount <= Constants.MAX_STABLECOIN_18_AMOUNT, "Unstake amount too large");

        _accruePool(btdPool);

        UserInfo storage user = btdUsers[msg.sender];
        uint256 pendingAmount = _pendingCurrent(btdPool, user);

        // Total available = principal + pending interest
        uint256 totalAvailable = user.amount + pendingAmount;
        require(totalAvailable >= amount, "Unstake exceeds balance");

        // Calculate interest and principal proportionally
        (uint256 interestShare, uint256 principalShare) = InterestMath.splitWithdrawal(
            amount,
            pendingAmount,
            totalAvailable
        );

        // Pay out interest portion
        if (interestShare > 0) {
            _payout(btdPool, msg.sender, interestShare);
            emit InterestClaimed(msg.sender, address(btdPool.token), interestShare);
        }

        // Withdraw principal portion
        if (principalShare > 0) {
            user.amount -= principalShare;
            btdPool.totalStaked -= principalShare;
            btdPool.token.safeTransfer(msg.sender, principalShare);
            emit Withdrawn(msg.sender, address(btdPool.token), principalShare);
        }

        user.rewardDebt = InterestMath.rewardDebtValue(user.amount, btdPool.accInterestPerShare);
    }

    /// @notice Unstakes BTB
    /// @dev Anyone can call, withdrawal amount can include pending interest
    ///      Proportionally allocates interest and principal, interest portion is minted, principal portion is transferred from contract balance
    /// @param amount BTB amount to unstake (can include pending interest), precision 1e18
    function unstakeBTB(uint256 amount) external nonReentrant {
        require(amount >= Constants.MIN_STABLECOIN_18_AMOUNT, "Unstake amount too small");
        require(amount <= Constants.MAX_STABLECOIN_18_AMOUNT, "Unstake amount too large");

        _accruePool(btbPool);

        UserInfo storage user = btbUsers[msg.sender];
        uint256 pendingAmount = _pendingCurrent(btbPool, user);

        // Total available = principal + pending interest
        uint256 totalAvailable = user.amount + pendingAmount;
        require(totalAvailable >= amount, "Unstake exceeds balance");

        // Calculate interest and principal proportionally
        (uint256 interestShare, uint256 principalShare) = InterestMath.splitWithdrawal(
            amount,
            pendingAmount,
            totalAvailable
        );

        // Pay out interest portion
        if (interestShare > 0) {
            _payout(btbPool, msg.sender, interestShare);
            emit InterestClaimed(msg.sender, address(btbPool.token), interestShare);
        }

        // Withdraw principal portion
        if (principalShare > 0) {
            user.amount -= principalShare;
            btbPool.totalStaked -= principalShare;
            btbPool.token.safeTransfer(msg.sender, principalShare);
            emit Withdrawn(msg.sender, address(btbPool.token), principalShare);
        }

        user.rewardDebt = InterestMath.rewardDebtValue(user.amount, btbPool.accInterestPerShare);
    }
}

project/contracts/interfaces/IInterestPo.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title IInterestPool - Standard interface for interest pool
 * @notice Defines the core functionality interface for the InterestPool contract
 * @dev Users can directly stake BTD/BTB to the interest pool without going through vault
 */
interface IInterestPool {
    // ============ BTD Operations ============

    /**
     * @notice Stake BTD to the interest pool
     * @param amount BTD amount
     */
    function stakeBTD(uint256 amount) external;

    /**
     * @notice Unstake BTD
     * @param amount BTD amount
     */
    function unstakeBTD(uint256 amount) external;

    // ============ BTB Operations ============

    /**
     * @notice Stake BTB to the interest pool
     * @param amount BTB amount
     */
    function stakeBTB(uint256 amount) external;

    /**
     * @notice Unstake BTB
     * @param amount BTB amount
     */
    function unstakeBTB(uint256 amount) external;

    // ============ Interest Rate Management ============

    /**
     * @notice Update BTD annual rate (read on-chain from FFR Oracle)
     * @dev No parameters needed, reads latest rate directly from FFR Oracle
     */
    function updateBTDAnnualRate() external;

    /**
     * @notice Update BTB annual rate (automatically adjusts based on BTB price)
     */
    function updateBTBAnnualRate() external;

    // ============ Events ============

    event Staked(address indexed user, address indexed token, uint256 amount);
    event Withdrawn(address indexed user, address indexed token, uint256 amount);
    event InterestClaimed(address indexed user, address indexed token, uint256 amount);
    event BTDAnnualRateUpdated(uint256 oldRateBps, uint256 newRateBps);
    event BTBAnnualRateUpdated(uint256 oldRateBps, uint256 newRateBps, uint256 price, int256 dailyChangeBps);
}

