// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title IMinter - Standard interface for Minter contract
 * @notice Defines the core functionality interface for the Minter contract
 */
interface IMinter {
    // --- Minting Operations ---
    /**
     * @notice Mint BTD stablecoin using WBTC
     * @param wbtcAmount Amount of WBTC
     */
    function mintBTD(uint256 wbtcAmount) external;

    // --- Redemption Operations ---
    /**
     * @notice Redeem BTD to receive WBTC, BTB, and BRS based on collateral ratio
     * @param btdAmount Amount of BTD to redeem
     */
    function redeemBTD(uint256 btdAmount) external;

    /**
     * @notice Exchange BTB for BTD at 1:1 ratio (requires CR>100%)
     * @param btbAmount Amount of BTB to exchange
     */
    function redeemBTB(uint256 btbAmount) external;

    // --- Query Functions ---
    // Note: getBTDPrice() and getBTBPrice() have been removed, please call PriceOracle.getBTDPrice() and getBTBPrice() directly

    /**
     * @notice Get system collateral ratio
     * @return Collateral ratio, 18 decimal precision (e.g., 1.5e18 represents 150%)
     */
    function getCollateralRatio() external view returns (uint256);

    /**
     * @notice Get ConfigCore contract address
     * @dev ConfigCore is immutable, cannot be changed after deployment
     * @return ConfigCore contract address
     */
    function configCore() external view returns (address);

    /**
     * @notice Get ConfigGov contract address
     * @dev ConfigGov can be updated via upgradeGov()
     * @return ConfigGov contract address
     */
    function configGov() external view returns (address);

    // --- Calculation Functions ---
    /**
     * @notice Calculate BTD mint amount and fee (without executing actual mint)
     * @param wbtcAmount Input WBTC amount, 8 decimal precision
     * @return btdAmount Mintable BTD amount, 18 decimal precision
     * @return fee Minting fee (denominated in BTD), 18 decimal precision
     */
    function calculateMintAmount(uint256 wbtcAmount) external view returns (uint256 btdAmount, uint256 fee);

    /**
     * @notice Calculate WBTC amount and fee when redeeming BTD (without executing actual redemption)
     * @param btdAmount Input BTD amount, 18 decimal precision
     * @return wbtcAmount Redeemable WBTC amount, 8 decimal precision
     * @return fee Redemption fee (denominated in WBTC), 8 decimal precision
     */
    function calculateBurnAmount(uint256 btdAmount) external view returns (uint256 wbtcAmount, uint256 fee);

    // --- Events ---
    event BTDMinted(address indexed user, uint256 wbtcAmount, uint256 btdAmount, uint256 fee);
    event BTDRedeemed(
        address indexed user,
        uint256 btdAmount,
        uint256 wbtcAmount,
        uint256 btbAmount,
        uint256 brsAmount
    );
    event BTBRedeemed(address indexed user, uint256 btbAmount, uint256 btdAmount);
}

project/contracts/interfaces/IPriceOracl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title IPriceOracle - Standard interface for price oracle
 * @notice Provides a unified price query interface for the entire BRS system
 * @dev All prices are returned with 18 decimal precision (1e18 = $1)
 */
interface IPriceOracle {
    // ============ Events ============

    event TWAPOracleUpdated(address indexed oldOracle, address indexed newOracle);
    event TWAPModeChanged(bool enabled);
    event PriceQueried(address indexed token, uint256 price, uint256 timestamp);

    // ============ Core Price Query Interface ============

    /**
     * @notice Get WBTC/USD price (multi-oracle verification)
     * @dev Aggregates Chainlink (WBTC/BTC & BTC/USD), Pyth, Redstone as reference,
     *      Uniswap TWAP/spot as final price, requires deviation <1% from reference median
     * @return price WBTC price, 18 decimal precision (e.g., $50,000 = 50000e18)
     */
    function getWBTCPrice() external view returns (uint256 price);

    /**
     * @notice Get BTD/USD actual market price
     * @dev Queries BTD's actual market price from Uniswap BTD/USDC pool
     *      Note: This is BTD's actual trading price, which may deviate from IUSD target price
     * @return price BTD price, 18 decimal precision
     */
    function getBTDPrice() external view returns (uint256 price);

    /**
     * @notice Get BTB/USD price
     * @dev Calculated via chain: BTB/BTD x BTD/USDC = BTB/USD
     * @return price BTB price, 18 decimal precision
     */
    function getBTBPrice() external view returns (uint256 price);

    /**
     * @notice Get BRS/USD price
     * @dev Calculated via chain: BRS/BTD x BTD/USDC = BRS/USD
     * @return price BRS price, 18 decimal precision
     */
    function getBRSPrice() external view returns (uint256 price);

    /**
     * @notice Get IUSD price (Ideal USD)
     * @dev Queries current IUSD value from IdealUSDManager contract
     * @return price IUSD price, 18 decimal precision
     */
    function getIUSDPrice() external view returns (uint256 price);

    /**
     * @notice Get USD price by token address (generic price query)
     * @dev Supports WBTC, BTD, BTB, BRS, USDC, USDT
     * @param token Token address
     * @return price Token's USD price, 18 decimal precision
     */
    function getPrice(address token) external view returns (uint256 price);

    /**
     * @notice Get generic token pair price
     * @dev Automatically uses TWAP (if enabled) or spot price
     * @param pool Uniswap V2 pair address
     * @param base Base token address
     * @param quote Quote token address
     * @return price Price (quote/base), 18 decimal precision
     */
    function getPrice(address pool, address base, address quote)
        external view returns (uint256 price);

    // ============ TWAP Management Interface ============

    /**
     * @notice Set TWAP Oracle address
     * @param _twapOracle UniswapV2TWAPOracle contract address
     */
    function setTWAPOracle(address _twapOracle) external;

    /**
     * @notice Enable or disable TWAP mode
     * @dev TWAP mode should be enabled in production, can be disabled in test environment
     * @param _useTWAP true=use TWAP (secure), false=use spot price (testing only)
     */
    function setUseTWAP(bool _useTWAP) external;

    /**
     * @notice Check if TWAP is enabled
     * @return enabled true=TWAP is enabled
     */
    function isTWAPEnabled() external view returns (bool enabled);

    /**
     * @notice Get TWAP Oracle address
     * @return oracle TWAP Oracle contract address
     */
    function getTWAPOracle() external view returns (address oracle);

    // ============ TWAP Update Functions ============

    /**
     * @notice Update TWAP for WBTC price (WBTC/USDC pair)
     */
    function updateTWAPForWBTC() external;

    /**
     * @notice Update TWAP for BTD price (BTD/USDC pair)
     */
    function updateTWAPForBTD() external;

    /**
     * @notice Update TWAP for BTB price (BTB/BTD + BTD/USDC pairs)
     */
    function updateTWAPForBTB() external;

    /**
     * @notice Update TWAP for BRS price (BRS/BTD + BTD/USDC pairs)
     */
    function updateTWAPForBRS() external;

    /**
     * @notice Update TWAP for all pairs
     */
    function updateTWAPAll() external;

    // ============ Chainlink Related ============

    /**
     * @notice Get Chainlink BTC/USD price
     * @dev Used only as validator, not as primary price source
     * @return price BTC/USD price, 18 decimal precision
     */
    function getChainlinkBTCUSD() external view returns (uint256 price);
}

project/contracts/libraries/Constants.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title Constants - BRS System Global Constants Library
 * @notice Centralized management of system-level immutable constants
 * @dev Uses library + internal constant to ensure compile-time inlining (zero gas overhead)
 *
 * Key features:
 * - internal constant is inlined by the compiler into contracts that use it
 * - Gas cost = 16 gas (same as local constant)
 * - Unified management, single source of truth
 * - Type-safe, compile-time checking
 */
library Constants {
    // ============ Precision Constants ============

    /// @notice 18 decimals precision (standard ERC20, USD prices)
    uint256 internal constant PRECISION_18 = 1e18;

    /// @notice 8 decimals precision (BTC)
    uint256 internal constant PRECISION_8 = 1e8;

    /// @notice 6 decimals precision (USDC/USDT)
    uint256 internal constant PRECISION_6 = 1e6;

    // ============ Precision Conversion Scale Constants ============

    /// @notice WBTC (8 decimals) to normalized (18 decimals) scale factor
    /// @dev 10^(18-8) = 1e10, used for explicit precision conversion, avoiding runtime EXP calculation
    uint256 internal constant SCALE_WBTC_TO_NORM = 1e10;

    /// @notice USDC (6 decimals) to normalized (18 decimals) scale factor
    /// @dev 10^(18-6) = 1e12, used for explicit precision conversion
    uint256 internal constant SCALE_USDC_TO_NORM = 1e12;

    /// @notice USDT (6 decimals) to normalized (18 decimals) scale factor
    /// @dev 10^(18-6) = 1e12, used for explicit precision conversion (same as USDC)
    uint256 internal constant SCALE_USDT_TO_NORM = 1e12;

    // Note: For normalized -> native conversion, use division by the same scale factor:
    // - Normalized to WBTC: amount / SCALE_WBTC_TO_NORM
    // - Normalized to USDC: amount / SCALE_USDC_TO_NORM
    // - Normalized to USDT: amount / SCALE_USDT_TO_NORM

    // ============ Minimum Operation Amount Constants ============

    /// @notice Minimum BTC operation amount per transaction (8 decimals)
    /// @dev 1 satoshi, prevents dust attacks
    uint256 internal constant MIN_BTC_AMOUNT = 1;

    /// @notice Minimum ETH operation amount per transaction (18 decimals)
    /// @dev 1e-8 ETH = 1e10 wei (0.00000001 ETH)
    uint256 internal constant MIN_ETH_AMOUNT = 1e10;

        /// @notice Minimum USD value for operations (18 decimals)
    /// @dev Used in Minter, Treasury, and other scenarios involving USD value conversion
    /// $0.001 USD, prevents dust attacks and precision loss
    ///
    /// Use cases:
    /// - Minter.mintBTD(): Check mint USD value >= $0.001
    /// - Minter.redeemBTD(): Check redeem USD value >= $0.001
    /// - Treasury.buyback(): Check buyback USD value >= $0.001
    uint256 internal constant MIN_USD_VALUE = 1e15;

    /// @notice Minimum operation amount for 6-decimal stablecoins
    /// @dev 0.001 USDC/USDT = 1000 units (6 decimals), prevents dust attacks
    /// Applicable to: USDC, USDT
    uint256 internal constant MIN_STABLECOIN_6_AMOUNT = 1000;

    /// @notice Minimum operation amount for 18-decimal stablecoins
    /// @dev 0.001 tokens = 1e15 (18 decimals), prevents dust attacks
    /// Applicable to: BTD, BTB, stBTD, stBTB
    ///
    /// Use cases:
    /// - InterestPool.stake(): Minimum stake amount for BTD/BTB
    /// - StakingRouter: Minimum stake amount for stBTD/stBTB
    uint256 internal constant MIN_STABLECOIN_18_AMOUNT = 1e15;

    // ============ Maximum Single Operation Limits (Prevents Hacker Attacks and Overflow) ============

    /// @notice Maximum WBTC amount per single operation
    /// @dev 10,000 BTC (8 decimals = 10000 * 1e8)
    /// Prevents hacker attacks and integer overflow, applies to all WBTC transfer/mint/redeem operations
    uint256 internal constant MAX_WBTC_AMOUNT = 10_000 * 1e8;

    /// @notice Maximum ETH amount per single operation
    /// @dev 100,000 ETH (18 decimals = 100000 * 1e18)
    /// Prevents hacker attacks and integer overflow
    uint256 internal constant MAX_ETH_AMOUNT = 100_000 * 1e18;

    /// @notice Maximum 6-decimal stablecoin amount per single operation
    /// @dev 1 billion USDC/USDT (6 decimals = 1000000000 * 1e6)
    /// Prevents hacker attacks and integer overflow
    /// Applicable to: USDC, USDT
    uint256 internal constant MAX_STABLECOIN_6_AMOUNT = 1_000_000_000 * 1e6;

    /// @notice Maximum 18-decimal stablecoin amount per single operation
    /// @dev 1 billion BTD/BTB/stBTD/stBTB (18 decimals = 1000000000 * 1e18)
    /// Prevents hacker attacks and integer overflow
    /// Applicable to: BTD, BTB, stBTD, stBTB and other 18-decimal stablecoins
    uint256 internal constant MAX_STABLECOIN_18_AMOUNT = 1_000_000_000 * 1e18;

    /// @notice Basis points base (10000 = 100.00%)
    uint256 internal constant BPS_BASE = 10000;

    // ============ Time Constants ============

    /// @notice Seconds per year
    uint256 internal constant SECONDS_PER_YEAR = 365 days;

    /// @notice Seconds per day
    uint256 internal constant SECONDS_PER_DAY = 1 days;

    /// @notice BTC halving cycle (4 years)
    uint256 internal constant ERA_PERIOD = 4 * 365 days;

    // ============ Inflation Parameter Constants ============

    /// @notice Fixed annual inflation rate - 2%
    /// @dev Fixed inflation target specified in whitepaper, used for IUSD (Ideal USD) calculation
    /// 2% = 0.02 = 2e16 (18 decimals)
    uint256 internal constant ANNUAL_INFLATION_RATE = 2e16;

    /// @notice Monthly growth factor - (1.02)^(1/12)
    /// @dev Calculated from 2% annual inflation
    /// Formula: (1 + 0.02)^(1/12) = 1.001651581301920174
    /// Calculated off-chain with high precision and stored with 18 decimals
    uint256 internal constant MONTHLY_GROWTH_FACTOR = 1001651581301920174;

    // ============ Supply Constants ============

    /// @notice BRS maximum supply (2.1 billion, tribute to BTC's 21 million)
    /// @dev Maximum supply of BRS tokens, the only definition in the system
    uint256 internal constant BRS_MAX_SUPPLY = 2_100_000_000e18;
}

project/contracts/libraries/InterestMath.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "./Constants.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title InterestMath - Interest Accumulation and Reward Distribution Calculation Library
 * @notice Pure function library encapsulating all interest accumulation and reward distribution calculations
 * @dev Provides mathematical calculation support for interest pools, staking rewards, and similar scenarios
 */
library InterestMath {


    /**
     * @notice Calculate pending rewards to be claimed
     * @dev Formula: Pending Rewards = (Staked Amount × Accumulated Interest Per Share) / 1e18 - Reward Debt
     *      Uses debt mechanism to prevent duplicate claims
     * @param amount Staked amount (18 decimals)
     * @param accInterestPerShare Accumulated interest per share (18 decimals)
     * @param rewardDebt Reward debt (18 decimals)
     * @return Pending reward amount (18 decimals)
     */
    function pendingReward(uint256 amount, uint256 accInterestPerShare, uint256 rewardDebt) internal pure returns (uint256) {
        if (amount == 0 || accInterestPerShare == 0) {
            return 0;
        }
        uint256 accumulated = Math.mulDiv(amount, accInterestPerShare, Constants.PRECISION_18);
        return accumulated > rewardDebt ? accumulated - rewardDebt : 0;
    }

    /**
     * @notice Calculate interest per share delta over a time period
     * @dev Formula: delta = (Annual Rate × Time) / (10000 × 365 days)
     *      - Annual rate is expressed in basis points (1% = 100 bps)
     *      - Time is in seconds
     *      - Returns delta value with 18 decimals
     * @param annualRateBps Annual rate (basis points, 100 = 1%)
     * @param timeElapsed Elapsed time (seconds)
     * @return Interest per share delta (18 decimals)
     */
    function interestPerShareDelta(uint256 annualRateBps, uint256 timeElapsed) internal pure returns (uint256) {
        if (annualRateBps == 0 || timeElapsed == 0) {
            return 0;
        }
        uint256 scaled = Math.mulDiv(annualRateBps, Constants.PRECISION_18, 1);
        scaled = Math.mulDiv(scaled, timeElapsed, 1);
        uint256 denominator = Constants.BPS_BASE * Constants.SECONDS_PER_YEAR;
        return Math.mulDiv(scaled, 1, denominator);
    }

    /**
     * @notice Calculate reward debt value
     * @dev Formula: Debt = (Staked Amount × Accumulated Interest Per Share) / 1e18
     *      Used to record initial debt when staking, preventing claims of historical rewards
     * @param amount Staked amount (18 decimals)
     * @param accInterestPerShare Accumulated interest per share (18 decimals)
     * @return Reward debt value (18 decimals)
     */
    function rewardDebtValue(uint256 amount, uint256 accInterestPerShare) internal pure returns (uint256) {
        if (amount == 0 || accInterestPerShare == 0) {
            return 0;
        }
        return Math.mulDiv(amount, accInterestPerShare, Constants.PRECISION_18);
    }

    /**
     * @notice Calculate fee amount
     * @dev Formula: Fee = (Amount × Fee Basis Points) / 10000
     *      - Fee rate is expressed in basis points (100 bps = 1%)
     * @param amount Original amount (any precision)
     * @param feeBps Fee basis points (100 = 1%)
     * @return Fee amount (same precision as input)
     */
    function feeAmount(uint256 amount, uint256 feeBps) internal pure returns (uint256) {
        if (amount == 0 || feeBps == 0) {
            return 0;
        }
        return Math.mulDiv(amount, feeBps, Constants.BPS_BASE);
    }

    /**
     * @notice Split withdrawal amount into interest and principal proportionally
     * @dev Formula:
     *      Interest Portion = (Withdrawal Amount × Pending Interest) / Total Available
     *      Principal Portion = Withdrawal Amount - Interest Portion
     *      Interest is used first, shortfall is covered by principal
     * @param amount Withdrawal amount (18 decimals)
     * @param pendingInterest Pending interest (18 decimals)
     * @param totalAvailable Total available amount (18 decimals)
     * @return interestShare Interest portion (18 decimals)
     * @return principalShare Principal portion (18 decimals)
     */
    function splitWithdrawal(uint256 amount, uint256 pendingInterest, uint256 totalAvailable) internal pure returns (uint256 interestShare, uint256 principalShare) {
        if (amount == 0) {
            return (0, 0);
        }
        if (totalAvailable == 0 || pendingInterest == 0) {
            return (0, amount);
        }
        interestShare = Math.mulDiv(amount, pendingInterest, totalAvailable);
        if (interestShare > amount) {
            interestShare = amount;
        }
        principalShare = amount - interestShare;
    }

    /**
     * @notice Calculate total assets including accrued interest
     * @dev Formula: Total Assets = Principal + (Principal × Interest Delta)
     *      Interest Delta = (Annual Rate × Elapsed Time) / (10000 × 365 days)
     *      Used for real-time queries of total assets including unsettled interest
     * @param principal Principal amount (18 decimals)
     * @param annualRateBps Annual rate (basis points)
     * @param lastAccrual Last settlement time (Unix timestamp)
     * @param currentTimestamp Current time (Unix timestamp)
     * @return Total assets (18 decimals)
     */
    function totalAssetsWithAccrued(
        uint256 principal,
        uint256 annualRateBps,
        uint256 lastAccrual,
        uint256 currentTimestamp
    ) internal pure returns (uint256) {
        if (principal == 0) {
            return 0;
        }
        if (currentTimestamp <= lastAccrual || annualRateBps == 0) {
            return principal;
        }
        uint256 elapsed = currentTimestamp - lastAccrual;
        uint256 delta = interestPerShareDelta(annualRateBps, elapsed);
        if (delta == 0) {
            return principal;
        }
        uint256 interest = Math.mulDiv(principal, delta, Constants.PRECISION_18);
        return principal + interest;
    }

    /**
     * @notice Calculate price change percentage (basis points)
     * @dev Formula: Change Rate = ((Current Price - Previous Price) / Previous Price) × 10000
     *      - Returns positive value for price increase
     *      - Returns negative value for price decrease
     *      - Returns 0 for unchanged price
     * @param previousPrice Previous price (18 decimals)
     * @param currentPrice Current price (18 decimals)
     * @return Price change rate (basis points, 100 = 1%)
     */
    function priceChangeBps(uint256 previousPrice, uint256 currentPrice) internal pure returns (int256) {
        if (previousPrice == 0 || currentPrice == previousPrice) {
            return 0;
        }
        if (currentPrice > previousPrice) {
            uint256 delta = Math.mulDiv(currentPrice - previousPrice, Constants.BPS_BASE, previousPrice);
            return int256(delta);
        }
        uint256 negDelta = Math.mulDiv(previousPrice - currentPrice, Constants.BPS_BASE, previousPrice);
        return -int256(negDelta);
    }
}

